import React, { useState } from 'react'
import {
  StyleSheet,
  Text,
  View,
  Dimensions,
  TouchableOpacity,
  StatusBar,
  Slider,
} from 'react-native'
import TextTicker from 'react-native-text-ticker'
import Entypo from 'react-native-vector-icons/Entypo'
import AntDesign from 'react-native-vector-icons/AntDesign'
import Foundation from 'react-native-vector-icons/Foundation'
//import Slider from 'react-native-slider'
// import Sound from 'react-native-sound'
// const audiolist = [
//   {
//     title: 'play music',
//     isRequire: true,
//     url: require('../list/Saybu Swag - Gecələr(MP3_160K).mp3'),
//   },
// ]

const Player = () => {
  const [plying, setplying] = useState(true)
  const [liked, setliked] = useState(false)
  const [loop, setloop] = useState(0)
  const loopicon = () => {
    if (loop === 0) {
      return (
        <TouchableOpacity style={{ top: 30 }}>
          <Foundation
            onPress={() => {
              if (loop === 2) {
                setloop(0)
              } else {
                setloop(loop + 1)
              }
            }}
            name="loop"
            color="#1D212C"
            size={40}
            style={{ alignSelf: 'center' }}
          />
        </TouchableOpacity>
      )
    } else if (loop === 1) {
      return (
        <TouchableOpacity style={{ }}>
          <Entypo
            onPress={() => {
              if (loop === 2) {
                setloop(0)
              } else {
                setloop(loop + 1)
              }
            }}
            name="shuffle"
            color="#1D212C"
            size={30}
            style={{ alignSelf: 'center' }}
          />
        </TouchableOpacity>
      )
    } else {
      return (
        <TouchableOpacity
          style={{ top: 30 }}
          onPress={() => {
            if (loop === 2) {
              setloop(0)
            } else {
              setloop(loop + 1)
            }
          }}
        >
          <Foundation name="loop" color="#1D212C" size={40} style={{}} />
          <Text
            style={{
              top: -28,
              left: 18,
              fontWeight: 'bold',
              color: '#1D212C',
              fontSize: 12,
            }}
          >
            1
          </Text>
        </TouchableOpacity>
      )
    }
  }
  return (
    <View style={styles.container}>
      <View style={styles.flat}>
        <View style={styles.textarea}>
          <TextTicker
            style={styles.trackheader}
            duration={7000}
            repeatSpacer={50}
          >
            TTM
          </TextTicker>
        </View>
        <View style={styles.textarea1}>
          <TextTicker
            style={styles.trackname}
            duration={9000}
            repeatSpacer={50}
          >
            xpert ft paster
          </TextTicker>
        </View>
      </View>
      <Slider
        style={styles.slider}
        minimumValue={0}
        maximumValue={10}
        value={8}
        minimumTrackTintColor="green"
        maximumTrackTintColor="red"
      />
      <View style={styles.CtrlBtns}>
        <View>{loopicon()}</View>
        <View style={styles.playingBtns}>
          <AntDesign
            name="banckward"
            color="black"
            size={25}
            style={{ alignSelf: 'center' }}
          />
          <View style={styles.playbtn}>
            {plying ? (
              <Entypo
                onPress={() => {
                  setplying(!plying)
                }}
                name="controller-play"
                color="white"
                size={60}
                style={{ marginLeft: 8, marginTop: 4 }}
              />
            ) : (
              <Entypo
                onPress={() => {
                  setplying(!plying)
                }}
                name="controller-paus"
                color="white"
                size={60}
                style={{ alignSelf: 'center', marginTop: 4 }}
              />
            )}
          </View>
          <AntDesign
            name="forward"
            color="black"
            size={25}
            style={{ alignSelf: 'center' }}
          />
        </View>
        <View>
          {liked ? (
            <Entypo
              onPress={() => {
                setliked(!liked)
              }}
              name="heart"
              color="#1D212C"
              size={30}
              style={{ alignSelf: 'center' }}
            />
          ) : (
            <Entypo
              onPress={() => {
                setliked(!liked)
              }}
              name="heart-outlined"
              color="#1D212C"
              size={30}
              style={{ alignSelf: 'center' }}
            />
          )}
        </View>
      </View>
    </View>
  )
}
const dimensions = {
  width: Dimensions.get('window').width,
  height: Dimensions.get('window').height,
}
const styles = StyleSheet.create({
  container: {
    marginTop: StatusBar.currentHeight,
    backgroundColor: '#DDD8DA',
    //#1D212C
    height: dimensions.height,
  },
  slider: {
    borderRadius: 100,
  },
  playbtn: {
    width: 70,
    height: 70,
    backgroundColor: '#1D212C',
    alignSelf: 'center',
    borderRadius: 100,
  },
  CtrlBtns: {
    width: dimensions.width,
    height: 100,
    backgroundColor: 'lightblue',
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  playingBtns: {
    width: 220,
    height: 50,
    // marginTop: 25,
    backgroundColor: 'white',
    borderRadius: 100,
    alignSelf: 'center',
    flexDirection: 'row',
    justifyContent: 'space-evenly',
  },

  trackheader: {
    textAlign: 'center',
    color: 'white',
    fontSize: 20,
  },
  textarea: {
    width: 170,
    alignSelf: 'center',
    top: 300,
  },
  textarea1: {
    width: 150,
    alignSelf: 'center',
    top: 300,
  },
  trackname: {
    textAlign: 'center',
    fontSize: 18,
    color: 'white',
    marginTop: 10,
  },
  flat: {
    width: 220,
    height: 450,
    backgroundColor: '#1D212C',
    alignSelf: 'center',
    borderBottomLeftRadius: 100,
    borderBottomRightRadius: 100,
  },
})
export default Player
